/**
 * Modules organize content of a specific type, for example Fiori Element UIs or CAP service definitions, in a project.
 * Modules are always associated with one directory.
 */
export default interface ModuleData {
    /**
     * Module type
     */
    type: string;
    /**
     * Module display name
     */
    name: string;
    /**
     * Module path
     */
    path: string;
    /**
     * Tags related to the Module
     */
    tags: string[];
    /**
     * Info messages
     */
    info?: any;
}
