/// <reference types="node" />
import { EventEmitter } from 'events';
import { ProjectFileSystem, FileSystemWatchManager, ChangeListener, Observer } from '@sap/artifact-management-base';
/**
 */
export default class LocalFileSystemWatchManager extends EventEmitter implements FileSystemWatchManager {
    private rootPath;
    private watcher?;
    private observers;
    private filePatterns;
    private eventTypes;
    private eventInQueue;
    constructor(rootPath: string);
    removeFileSystemWatcher(observersToDel: Observer[]): void;
    createFileSystemWatcher(fs: ProjectFileSystem, globPatterns: string[], listener: ChangeListener): Observer[];
    createObserver(fs: ProjectFileSystem, globPattern: string, listener: ChangeListener): Observer;
    addObserver(fs: ProjectFileSystem, globPattern: string, listener: ChangeListener): Observer;
    logObservers(): void;
    destroy(): void;
    enable(): Promise<void>;
    disable(): void;
    watchEvent(event: string, filename: string): Promise<void>;
}
