import { Uri, WorkspaceFolder } from 'vscode';
import { KeyIn, Tag } from '@sap/artifact-management-base';
import ProjectApi from './ProjectApi';
export default interface WorkspaceApi {
    getProjects(tag?: KeyIn<typeof Tag>): Promise<ProjectApi[]>;
    /**
     * Get all paths that contain projects in the workspace
     */
    getProjectUris(): Promise<Uri[]>;
    startWatch(): void;
    stopWatch(): void;
    onWorkspaceChanged(handler: (event: string, folders: WorkspaceFolder[]) => void): void;
}
