// Valiate that the version in package.json is equal to the version in versions.json
const fs = require('fs');

// Get version from package.json
let packagePath = __dirname + '/../package.json';
let packageString = fs.readFileSync(packagePath);
let packageObj = JSON.parse(packageString);

// Get version from version.json
let versionPath = __dirname + '/../src/version.json';
let versionString = fs.readFileSync(versionPath);
let versionObj = JSON.parse(versionString);

if (packageObj.version != versionObj.version) {
    throw new Error('Version mismatch between package.json (' + packageObj.version + ') and version.json (' + versionObj.version + '). See readme for instructions');
}
console.log("Versions are equals");




